import 'bootstrap/dist/css/bootstrap.min.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import React, { useState } from 'react';
import Axios from 'axios'
import { response } from 'express';



function registration() {
    const [inputs, setInputs] = useState({});
    const handlechange = (val) => {
        const name = val.target.name;
        const value = val.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }
    const handleSubmit = (event) => {
        event.preventDefault();
        // console.log(names);

        var password = inputs.Password;
        var confirmpass = inputs.ConfirmPassword;
        if (confirmpass !== password) {
            alert("Confirm Password not matches to the given Password");
        } else {
            // console.log(inputs);
            Axios.post('http://localhost:5000/register', {
                Name: inputs.username,
                PhoneNumber: inputs.PhoneNumber,
                Email: inputs.Email,
                Password: inputs.Password,
                ConfirmPassword: inputs.ConfirmPassword

            }).then((response) =>{

            })
        }
    }

    return (
        <div className="Image">
            <div className='reg_margin'>
                <form className='mt-0 ' style={{ width: '30rem' }} onSubmit={handleSubmit}>
                    <h3 className='text-center mt-4 mb-4'><b >Sign Up</b></h3>
                    <Form.Group className="mb-3" controlId="name">
                        <Form.Label><b>Name</b></Form.Label>
                        <Form.Control type="text" placeholder="Name" name='username' value={inputs.username}
                            onChange={handlechange} />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="phonenumber">
                        <Form.Label><b>Phone Number</b></Form.Label>
                        <Form.Control type='tel' placeholder=" Phone Number" name='PhoneNumber' value={inputs.PhoneNumber} onChange={handlechange} />
                    </Form.Group>

                    <Form.Group className="mb-3" controlId="Email">
                        <Form.Label><b>Email address</b></Form.Label>
                        <Form.Control type="email" placeholder="Email" name='Email' value={inputs.Email}
                            onChange={handlechange} />
                    </Form.Group>


                    <Form.Group className="mb-3" controlId="password">
                        <Form.Label><b>Password</b></Form.Label>
                        <Form.Control type="password" placeholder="Password" name='Password' value={inputs.Password}
                            onChange={handlechange} />

                    </Form.Group>
                    <Form.Group className="mb-3" controlId="confirm_password">
                        <Form.Label><b>Confirm Password</b></Form.Label>
                        <Form.Control type="password" placeholder="Confirm Password" name='ConfirmPassword' value={inputs.ConfirmPassword}
                            onChange={handlechange} />

                    </Form.Group>
                    <Button variant="primary" type="submit" className='button'>
                        Register
                    </Button>
                </form>
            </div>
        </div>
    );
}
export default registration;
