import Button from 'react-bootstrap/esm/Button';
import redme from '../Images/Redme.jpg';
import React  from 'react';
function Redme() {
    return (
        <div className="Product">
            <div className='oppo1'>
                <img src={redme} alt="redme" className='button' />

                <Button variant="warning" className='oppo_confirm' >Confirm</Button>
            </div>
            <div className='product2'>
                <b>About this item</b><br></br>
                <b>•</b> Display: 90Hz FHD+ (1080x2400) AMOLED display; 16.33 centimeters (6.43 inch); 20:9 aspect ratio<br></br>
                <b>•</b>Camera: 50 MP Quad Rear camera with 8MP Ultra-wide, 2MP Macro and Portrait lens| 13 MP Front camera<br></br>
                <b>•</b>Battery: 5000 mAh large battery with 33W Pro fast charger in-box and Type-C connectivity<br></br>
                <b>•</b>Processor: Qualcomm Snapdragon 680 Octa-core; 6nm processor; Up to 2.4GHz clock speed<br></br>
                <b>•</b>Memory, Storage & SIM: 6GB RAM | 128GB UFS 2.2 storage expandable up to 512GB with dedicated SD card slot | Dual SIM (nano+nano) dual standby (4G+4G)<br></br>
          
            </div>


        </div>



    );
}
export default Redme;
