import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import React, { useState } from 'react';
import { HashLink as Link } from 'react-router-hash-link';
import Axios from 'axios'
// import { response } from 'express';


function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

   
    const submitGet = () => {

        Axios.get('http://localhost:5000/login', {
            params: {
                Email: email,
                Password: password
            }
        })
            .then(function (response) {
                console.log(response);
                foreach(re)
            })

    }


    return (
        <div className='Image'>

            <div className='margin'>
                <form className='mt-0 ' style={{ width: '30rem' }}>
                    <h3 className='text-center mt-4 mb-4'><b >Sign In</b></h3>

                    <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label><b>Email address</b></Form.Label>
                        <Form.Control type="email" placeholder="Email" onChange={(e) => {
                            setEmail(e.target.value)
                        }} />

                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBasicPassword">
                        <Form.Label><b>Password</b></Form.Label>
                        <Form.Control type="password" placeholder="Password" onChange={(e) => {
                            setPassword(e.target.value)
                        }} />
                    </Form.Group>
                    <Button variant="primary" type="submit" className='button' onClick={submitGet}>
                        Login
                    </Button>
                    <p className='text-center mt-4 mb-4'><b>Don't have an account please.</b>
                        <Link to="/Registration"><b>Sign Up</b></Link>

                    </p>



                </form>
            </div>

        </div>
    );
}
export default Login;
